#!/usr/bin/env sh

# Synopsis:
# Test the representer by running it against a predefined set of solutions
# with an expected output.

# Output:
# Outputs the diff of the expected representation and mapping against the
# actual representation and mapping generated by the representer.

# Example:
# ./bin/run-tests.sh

exit_code=0

# We need to copy the fixtures to a temp directory as the user
# running within the Docker container does not have permissions
# to write to the test_data directory
test_data_dir="test_data"
tmp_test_data_dir="/tmp/test_data"
rm -rf "${tmp_test_data_dir}"
mkdir -p "${tmp_test_data_dir}"
cp -R ${test_data_dir}/* "${tmp_test_data_dir}"

# Iterate over all test data directories
for test_dir in "${tmp_test_data_dir}"/*; do
    test_dir_name=$(basename "${test_dir}")
    test_dir_path=$(realpath "${test_dir}")

    bin/run.sh "${test_dir_name}" "${test_dir_path}" "${test_dir_path}"

    file="representation.txt"
    expected_file="expected_representation.ex"
    echo "${test_dir_name}: comparing ${file} to ${expected_file}"

    if ! diff "${test_dir_path}/${file}" "${test_dir_path}/${expected_file}"; then
        exit_code=1
    fi

    file="mapping.json"
    expected_file="expected_${file}"
    echo "${test_dir_name}: comparing ${file} to ${expected_file}"

    if ! diff "${test_dir_path}/${file}" "${test_dir_path}/${expected_file}"; then
        exit_code=1
    fi
done

exit ${exit_code}
